import React, {useEffect} from "react";
import { useParams } from 'react-router-dom';
import {searchMovie} from "backend/idm";
import {useUser} from "../hook/User";

const GetMovie = () => {
    const {
        accessToken, setAccessToken,
        refreshToken, setRefreshToken
    } = useUser();

    const [movie, setMovie] = React.useState({});
    const {movieId} = useParams();

    const getMovie = () => {
        searchMovie({}, accessToken, movieId)
            .then(response => setMovie(response.data))
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)));
    }

    useEffect(() => getMovie(), []);
    console.log(movie);
    return (
        <div>
            <div className="cart-details">
                { movie["movie"] && <img src={"https://image.tmdb.org/t/p/w185" + movie.posterPath} alt="" id="img"/>}
                { movie["movie"] && <h4 className="movie-detail">Title: {movie["movie"].title}</h4>}
                { movie["movie"] && <h4 className="movie-detail">Director: {movie["movie"].director}</h4>}
                { movie["movie"] && <h4 className="movie-detail">Rating: {movie["movie"].rating}</h4>}
                { movie["movie"] && <h4 className="movie-detail">Revenue: {movie["movie"].revenue}</h4>}
                { movie["movie"] && <h4 className="movie-detail">Budget: {movie["movie"].budget}</h4>}
                { movie["movie"] && <h4 className="movie-detail">Overview: {movie["movie"].overview}</h4>}

                {movie["genres"]?.map(genre =>
                    <h4 className="movie-detail">Genres: {genre.name}</h4>
                )}
            </div>
            <button  id="cartButton">Add To Cart</button>
            <button  id="pageButton">0</button>
            <button  id="pageButton">Remove</button>
        </div>
    );
}

export default GetMovie;
