import React, {useEffect} from "react";
import { useParams } from 'react-router-dom';
import {searchMovie} from "backend/idm";
import {useUser} from "../hook/User";

const GetMovie = () => {
    const {
        accessToken, setAccessToken,
        refreshToken, setRefreshToken
    } = useUser();

    const [movie, setMovie] = React.useState([]);
    const {movieId} = useParams();

    const getMovie = () => {
        searchMovie({}, accessToken, movieId)
            .then(response => setMovie(response.data))
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)));
    }

    useEffect(() => getMovie(), []);
    console.log(movie["movie"]);
    console.log(movie["genres"]);
    return (
        <div>
            <div className="cart-details">
                {movie["movies"]}
                <h4 className="movie-detail">Title: </h4>
                <h4 className="movie-detail">Director:</h4>
                <h4 className="movie-detail">Rating:</h4>
                <h4 className="movie-detail">Budget:</h4>
                <h4 className="movie-detail">Revenue:</h4>
                <h4 className="movie-detail">Overview:</h4>
            </div>
            <button  id="cartButton">Add To Cart</button>
            <button  id="pageButton">0</button>
            <button  id="pageButton">Remove</button>
        </div>
    );
}

export default GetMovie;
