import React, {useEffect} from "react";
import {useUser} from "../hook/User";
import {retrieveCart} from "../backend/idm";
import {useNavigate} from "react-router-dom";
import {useForm} from "react-hook-form";

const Cart = () => {
    const {
        accessToken, setAccessToken,
        refreshToken, setRefreshToken
    } = useUser();

    const [cart, setCart] = React.useState([]);
    const {register, getValues, handleSubmit} = useForm();
    const navigate = useNavigate();

    const viewCart = () => {
        retrieveCart({}, accessToken)
            .then(response => setCart(response.data))
            .catch(error => alert(JSON.stringify(error.response.data, null, 2)));
    }

    const backHome = () => {
        navigate("/");
        window.location.reload();
    }

    const updateCart = () => {
        const quantity = getValues("quantity");

        const payload = {

            quantity: quantity
        }
        // console.log(movieId);
        console.log(quantity);
    }

    useEffect(() => viewCart(), []);
    console.log(cart);
    return (
        <div className="cart-container">
            <div className="cart-header">Shopping Cart</div>
            <div className="cart-details">
                <h4 className="movie-details">Movie</h4>
                <h4 className="quantity-details">Quantity</h4>
                <h4 className="price-details">Price</h4>
            </div>
            <hr className="cart-line"/>

            <div id="cart-items">
                {cart["items"]?.map(movie =>
                     <div>
                         <img src={"https://image.tmdb.org/t/p/w185" + movie.posterPath} alt="" className="img-item"/>
                         <select className="quantity-item" defaultValue={movie.quantity} {...register("quantity")}>
                            <option value={1}>1</option>
                            <option value={2}>2</option>
                            <option value={3}>3</option>
                            <option value={4}>4</option>
                             <option value={5}>5</option>
                            <option value={6}>6</option>
                            <option value={7}>7</option>
                            <option value={8}>8</option>
                             <option value={9}>9</option>
                            <option value={10}>10</option>
                        </select>
                         <button onClick={handleSubmit(updateCart)} className="update-cart">Update</button>
                         <h4 className="price-item">${movie.unitPrice}</h4>
                     </div>
                )}
                <div id="checkout-items">
                    { cart["total"] && <h2 id="cart-total">Total: ${cart["total"]}</h2>}
                    <button id="cartButton">Checkout</button>
                    <button onClick={() => backHome()} id="pageButton">Home</button>
                </div>

            </div>
        </div>
    );
};

export default Cart;
